import React from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>
          My todo app
        </h1>
      </header>
      <form>
        <p>Add a new:</p>
        <input
          type="text"
        />
      </form>
    </div>
  );
}

function IdiomaticReactList(props) {
  return (
    <div>
      {props.items.map((item, index) => (
        <Item key={index} item={item} />
      ))}
    </div>
  );
}

export default App;
