import React, { useState, useEffect } from 'react';
import del from 'icons/delete.png';
import add from 'icons/add.png';
import check from 'icons/check.png';
import collapse from 'icons/collapse.png';
import expand from 'icons/expand.png';
const App = () => {
  // STATE
  const [todos, setTodos] = useState([]);

  // ON PAGE LOAD
  useEffect(() => {
    if (localStorage.getItem('todos') === null) {
      localStorage.setItem('todos', JSON.stringify([]));
    } else {
      setTodos(JSON.parse(localStorage.getItem('todos')));
    }
  }, []);

  // ON TODOS CHANGE
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  // FUNCTIONS
  const addNewTodo = (newBody) => {
    setTodos([...todos, { id: newID(), body: newBody, completed: false }]);
  };

  let lastId = 0;
  const newID = () => {
    lastId++;
    return `${lastId}`;
  };

  const removeTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const completeTodo = (id) => {
    setTodos(
      todos.map((todo) => {
        if (todo.id === id) {
          return { ...todo, completed: !todo.completed };
        }
        return todo;
      })
    );
  };

  // JSX
  return (
    <div className='relative h-full flex flex-col text-center'>
      <Header />
      <div className='flex-grow container mx-auto m-6'>
        <div className='mx-3'>
          <AddTodo addNewTodo={addNewTodo} />
          {todos.length > 0 ? (
            <TodoList
              todos={todos}
              complete={completeTodo}
              remove={removeTodo}
            />
          ) : (
            <p className='font-mono'>
              All done!
            </p>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

const AddTodo = ({ addNewTodo }) => {
  const setUserInput = (e) => {
    e.preventDefault();
    addNewTodo(e.target.input.value);
    e.target.input.value = '';
  };

  // JSX
  return (
    <div className='max-w-xl mx-auto my-8 p-2 shadow-lg rounded-xl bg-white relative'>
      <form action='' onSubmit={setUserInput}>
        <input
          name='input'
          type='text'
          placeholder='Add Todo...'
          className='p-2 w-full shadow rounded-lg focus:bg-gray-200 outline-none hover:bg-gray-200 hover:border-gray-800'
          required
        />
        <button
          name='submit'
          type='submit'
          title='Add Todo'
          className='sm:absolute sm:right-0 sm:bottom-0 bg-gray-800 text-white bg-gradient-to-r hover:from-blue-600 hover:to-green-600 rounded-full mt-4 p-1 px-10 sm:p-0 shadow hover:shadow-lg border border-gray-700 transform sm:translate-x-1 sm:translate-y-1 hover:scale-125 cursor-pointer'
        >
          <img src={add} alt='+'/>
        </button>
      </form>
    </div>
  );
};

const TodoList = ({ todos, complete, remove }) => {
  // STATE
  const [reverse, setReverse] = useState(true);
  const [hideCompleted, setHideCompleted] = useState(true);
  const [printTodos, setPrintTodos] = useState(todos);

  // EFFECT
  useEffect(() => {
    if (hideCompleted) {
      setPrintTodos(
        reverse
          ? todos
              .slice(0)
              .reverse()
              .filter((todo) => !todo.completed)
          : todos.filter((todo) => !todo.completed)
      );
    } else {
      setPrintTodos(reverse ? todos.slice(0).reverse() : todos);
    }
  }, [hideCompleted, reverse, todos]);

  // FUNCTIONS
  const reverseOrder = () => {
    setReverse(!reverse);
  };
  const toggleCompleted = () => {
    setHideCompleted(!hideCompleted);
  };

  // JSX
  return (
    <div className='relative max-w-xl mx-auto my-8 shadow-lg rounded-xl bg-white'>
      <div
        onClick={toggleCompleted}
        className={`absolute top-0 left-0 text-white hover:text-blue-600 m-2 cursor-pointer transform bg-gray-900 rounded-full p-1 hover:bg-white ${
          hideCompleted ? 'bg-green-500 scale-125' : ''
        }`}
        title='Hide Completed'
      >
        <img src={collapse} alt='collapse'/>
      </div>
      <div
        onClick={reverseOrder}
        className='absolute top-0 right-0 text-white hover:text-blue-600 m-2 cursor-pointer bg-gray-900 rounded-full p-1 hover:bg-white'
        title='Reverse Order'
      >
        <img src={expand} alt='expand'/>
      </div>
      <h2 className='bg-gray-700 text-white rounded-t-xl py-2'>TODOS</h2>
      <ul className='m-2 p-2'>
        {printTodos.map((todo) => (
          <Todo
            key={todo.id}
            id={todo.id}
            body={todo.body}
            completed={todo.completed}
            complete={complete}
            remove={remove}
          />
        ))}
      </ul>
    </div>
  );
};

const Todo = ({ id, body, completed, complete, remove }) => {
  // FUNCTIONS
  const removeTodo = () => {
    remove(id);
  };
  const completeTodo = () => {
    complete(id);
  };

  // JSX
  return (
    <div className='flex flex-row justify-between items-center my-2 p-1 bg-gray-200 rounded-full'>
      <div title='Mark as complete!' onClick={completeTodo}>
      <img src={check} alt='check'/>
      </div>
      <p
        className={`text-left w-full overflow-y-scroll p-2 ${
          completed ? 'line-through text-gray-400' : 'text-black'
        }`}
      >
        {body}
      </p>
      <div title='Delete me!' onClick={removeTodo}>
      <img src={del} alt='delete'/>
      </div>
    </div>
  );
};

const Header = () => {
  return (
    <header>
    </header>
  );
};


const Footer = () => {
  return (
    <footer>
    </footer>
  );
};

export default App;
