import React, { useState } from "react";
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>
          My todo app
        </h1>
      </header>
      <form>
        <p>Add a new todo:</p>
        <input
          type="text"
        />
      </form>
      <h2>
          My todos:
        </h2>
        <div className="List">
        <TodoList />
        </div>
    </div>
  );
}

function TodoList() {
  const initialTodos = ["Buy groceries", "Feed the chickens"];
  const [todos, setTodos] = useState(initialTodos);
  return (
    <ul>
      {todos.map((todo) => (
        <li>
          <input type="checkbox" /> {todo}
        </li>
      ))}
    </ul>
  );
}


export default App;
