import React from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>
          My todo app
        </h1>
      </header>
      <form>
        <p>Add a new todo:</p>
        <input
          type="text"
        />
      </form>
      <Itemlist items={items} />
    </div>
  );
}

function Itemlist(props) {
  const items = props.items;
  const listItems = items.map((number) =>
    <li key={number.toString()}>
      {number}
    </li>
  );
  return (
    <ul>{listItems}</ul>
  );
}

const items = [1, 2, 3, 4, 5];


export default App;
